import { useState, useEffect } from "react";
import { Button, TextField, Card, CardContent } from '@mui/material';

export default function SeatingPlan() {
  const [people, setPeople] = useState([]);
  const [groups, setGroups] = useState([]);
  const [newPerson, setNewPerson] = useState("");
  const [newGroup, setNewGroup] = useState("");
  const [seatingPlan, setSeatingPlan] = useState([]);

  useEffect(() => {
    fetch("/get_people").then(res => res.json()).then(setPeople);
    fetch("/get_groups").then(res => res.json()).then(setGroups);
  }, []);

  const addPerson = async () => {
    const res = await fetch("/add_person", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newPerson })
    });
    if (res.ok) {
      setNewPerson("");
      fetch("/get_people").then(res => res.json()).then(setPeople);
    }
  };

  const addGroup = async () => {
    const res = await fetch("/add_group", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: newGroup })
    });
    if (res.ok) {
      setNewGroup("");
      fetch("/get_groups").then(res => res.json()).then(setGroups);
    }
  };

  const generateSeating = async () => {
    const res = await fetch("/generate_seating", { method: "POST" });
    if (res.ok) {
      const data = await res.json();
      setSeatingPlan(data.seating_plan);
    }
  };

  return (
    <div className="p-4 space-y-4">
      <Card>
        <CardContent className="space-y-2">
          <h2 className="text-xl font-bold">Add Person</h2>
          <Input value={newPerson} onChange={(e) => setNewPerson(e.target.value)} placeholder="Person Name" />
          <Button onClick={addPerson}>Add</Button>
        </CardContent>
      </Card>

      <Card>
        <CardContent className="space-y-2">
          <h2 className="text-xl font-bold">Add Group</h2>
          <Input value={newGroup} onChange={(e) => setNewGroup(e.target.value)} placeholder="Group Name" />
          <Button onClick={addGroup}>Add</Button>
        </CardContent>
      </Card>

      <Button onClick={generateSeating} className="w-full">Generate Seating Plan</Button>

      <Card>
        <CardContent>
          <h2 className="text-xl font-bold">Seating Plan</h2>
          <ul>
            {seatingPlan.map((seat, index) => (
              <li key={index}>{seat.person} â†’ Table {seat.table}</li>
            ))}
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}
